        event.preventDefault()
        $(this).tab('show')
    })

    $(document).ready(() => {
        $('input[type="number"]').attr({
            max: 10,
            min: 0
        })
    })

    let editor_multi_select = $('.editor-multi-select')
    let languages
    if ($('#languages').val()) {
        languages = JSON.parse($('#languages').val())
    }
    let getLanguages

    $('#editorModal').on('show.bs.modal', () => {
        if (!getLanguages) getEditorLanguages()
    })

    const getEditorLanguages = () => {
        $.ajax({
            type: "GET",
            url: `/user/language`,
            processData: false,
            dataType: 'json',
            success: (data) => {
                if (data?.success === true) {
                    getLanguages = true
                    $('.editor-multi-select').attr('multiple', 'multiple')
                    $('.editor-multi-select').attr('disabled', false)
                    let allLanguages = []
                    if (data?.data?.length > 0) {
                        data?.data?.forEach(lan => {
                            allLanguages?.push({
                                text: lan?.name,
                                value: lan?.value
                            })
                        })
                        editor_multi_select.multipleSelect({
                            placeholder: "Select language",
                            data: allLanguages
                        })
                        if (languages && languages?.length > 0) {
                            editor_multi_select.multipleSelect('setSelects', languages?.map(lang => { return lang?.value }))
                        }
                    }
                }
            }
        })
    }

    $('#slider-onoffswitch').on('change', function () {
        const checked = $(this).is(':checked')

        $.ajax({
            type: "PUT",
            url: "/user/preferences",
            data: JSON.stringify({ slider: checked }),
            contentType: 'application/json',
            processData: false,
            dataType: 'json',
            success: (data) => {
                $.notify(` Settings Updated Successfully`, { type: "success", icon: "check" });
                if (checked === true) {
                    $('.audiobook-sliders').show()
                    $('.audiobook-nonslider').hide()
                } else {
                    $('.audiobook-sliders').hide()
                    $('.audiobook-nonslider').show()
                }
            }
        })
    })

    const handlePausesListDisplay = (list) => {
        const { comma, dash, question, exclamation, period, betweenParagraphs } = list

        $('#pause-comma').prop('checked', comma?.checked ? 'true' : '')
        $('#pause-comma').val(comma.value)

        $('#pause-question').prop('checked', question?.checked ? 'true' : '')
        $('#pause-question').val(question.value)

        $('#pause-exclamation').prop('checked', exclamation?.checked ? 'true' : '')
        $('#pause-exclamation').val(exclamation.value)

        $('#pause-period').prop('checked', period?.checked ? 'true' : '')
        $('#pause-period').val(period.value)

        $('#pause-between-paragraphs').prop('checked', betweenParagraphs?.checked ? 'true' : '')
        $('#pause-between-paragraphs').val(betweenParagraphs.value)
    }

    $('#pause-onoffswitch').on('change', function () {
