    const renderData = async (page = 1, limit = 5) => {
        $('.voice_profile_table tbody').html('')
        const result = await getVoiceProfiles(page, limit)
        const profiles = result?.data?.profiles
        const count = result?.data?.count
        pages = Math.ceil(count / limit)

        if (profiles && profiles?.length > 0) {
            let html = ''
            for (var i = 0; i < profiles?.length; i++) {
                html += `<tr>
                    <td>${profiles[i].name}</td>
                    <td>${profiles[i].Engine}</td>
                    <td>${profiles[i]?.Voice}</td>
                    <td>
                        <button class="d-inline-block btn btn-sm btn-info" onclick='useVoiceProfile("${profiles[i]?._id}", this)'>Use</button>
                        <span style="cursor: pointer;" onclick="deleteVoiceProfile('${profiles[i]?._id}', this)" class="text-danger actions" target="_blank"><i class="fas fa-lg mr-1 fa-trash-alt"></i></span>
                    </td>
                </tr>`
            }
            $('.voice_profile_table tbody').html(html)
        } else {
            $('.voice_profile_table tbody').html('<div class="m-3">No profiles found</div>')
        }
        buildPagination(page, count, limit, 'voice-profile-pagination', 'onVoiceProfilePrev', 'onVoiceProfilePageClick', 'onVoiceProfileNext')
    }

